    public int[] findSquare(int[][] matrix) {
        int len = matrix.length;
        if (len == 0) {
            return new int[0];
        }
        int[][] matHeight = new int[len][len];
        int[][] matWidth = new int[len][len];
        for (int i = 0; i < len; i++) {
            for (int j = 0; j < len; j++) {
                if (matrix[i][j] == 1) {
                    continue;
                }
                matHeight[i][j] = i > 0 ? matHeight[i - 1][j] + 1 : 1;
                matWidth[i][j] = j > 0 ? matWidth[i][j - 1] + 1 : 1;
            }
        }
        //System.out.println(Arrays.deepToString(matHeight));
        //System.out.println(Arrays.deepToString(matWidth));

        int r = 0, c = 0, size = 0;
        for (int i = 0; i < len; i++) {
            for (int j = 0; j < len; j++) {
                int min = Math.min(matHeight[i][j], matWidth[i][j]);
                for (int k = min; k > 0; k--) {
                    if (matWidth[i - k + 1][j] >= k && matHeight[i][j - k + 1] >= k && k > size) {

                        size = k;
                        r = i - k + 1;
                        c = j - k + 1;
                        System.out.println(String.format("%d %d %d", r, c, size));
                        break;
                    }
                }
            }
        }

        return size == 0 ? new int[0] : new int[]{r, c, size};
    }
